    // state = {
    //     fakeTweet: this.getFakeTweet(),
    //     correct: 0,
    //     total: 0,
    //     accuracy: 0,
    //     allTweets: [this.getRandTweets()]  
    // };

    state = {
      fakeTweet: " ",
      tweet1: " ",
      tweet2: " ",

      correct: 0,
      total: 0,
      accuracy: 0,
      allTweets: [" ", " ", " "]  
    };



    componentDidMount(){

      this.getTweets();
      this.renderTweetBox();

      // console.log("Comp: ", this.state.allTweets)
      // console.log("Comp1: ", this.state.allTweets[0])
      // console.log("Comp2: ", this.state.allTweets[1])
      // console.log("Comp3: ", this.state.allTweets[2])

      shuffle(this.state.allTweets);
   }



    getTweets(){
        fetch('/tweet').then(res => res.json()).then(data => {
          this.setState({fakeTweet: "FAKE!! -- " + data.tweet});
          // this.state.allTweets[0]=(data.tweet);

          // this.state.allTweets[0]=(this.state.fakeTweet);

        });

        fetch('/random').then(res => res.json()).then(data => {
          this.setState({tweet1: data.random});

          // this.setState({fakeTweet[1]:data.random });
          // this.state.allTweets[1]=(data.random);
        });

        fetch('/random').then(res => res.json()).then(data => {
          this.setState({tweet2: data.random});

          // this.state.allTweets[2]=(data.random);
        });

        // this.test();
        // this.setState({fakeTweet: this.state.allTweets[0]});
        //  this.state.allTweets[0]=(this.state.fakeTweet);


        shuffle(this.state.allTweets);
        // shuffle(this.state.allTweets);




        // this.state.allTweets.push(this.state.fakeTweet);

        // this.setState(_.shuffle(this.state.allTweets));
        // setAllTweets([fakeTweet, random, random2]);
    }



    counter(num){
      console.log(num, this.state.allTweets[num])
      console.log(this.state.fakeTweet)

      console.log(this.state.allTweets[num] == this.state.fakeTweet)

      if (this.state.allTweets[num] === this.state.fakeTweet){
        this.setState({correct: this.state.correct+1});
      }

      this.setState({total: this.state.total+1});
      this.setAccuracy();

    }

    setAccuracy(){

      var num = (this.state.total === 0) ? 0 : (((this.state.correct/this.state.total)*100).toPrecision(4));
      this.setState({accuracy: num});
    }



    renderTweetBox(){
      var test = [this.state.fakeTweet, this.state.tweet1, this.state.tweet2];

      console.log(test);

        return (
            <div className="tweet-container">   
                <div onClick={()=> this.counter(0)}>
                  <TweetBox text={this.state.fakeTweet}/>
                </div> 

                <div onClick={()=> this.counter(1)}>
                  < TweetBox text={this.state.tweet1}/>
                </div>

                <div onClick={()=> this.counter(2)}>
                  <TweetBox text={this.state.tweet2}/>
                </div>



              {/* <div onClick={()=> this.counter(0)}>
                <TweetBox text={this.state.allTweets[0]}/>
              </div> 

              <div onClick={()=> this.counter(1)}>
                < TweetBox text={this.state.allTweets[1]}/>
              </div>

              <div onClick={()=> this.counter(2)}>
                <TweetBox text={this.state.allTweets[2]}/>
              </div> */}
            </div>
          );

    }


    render (){
      return (

          <div className="App">
            <header className="header">
              <img src={bird} className="trump-bird" alt="logo" />
            </header>


            <div className="grid-container">
                <div className="left-panel">
                  <p className="desc">
                    Using a corpus of the entire collection of
                    Donald Trump's Tweets, this app generates a Markov chain. 
                  </p>

                  <p className="desc">
                    From the Markov model, a sentence (or Tweet in this case)
                    is created.

                  </p>

                  <p className="desc">
                    The fake Tweet generated from the Markov chain is placed
                    amongst two real Tweets randomly selected from Donald Trump's 
                    entire Tweet history.
                  </p>

                  <p className="desc">
                  Can you pick the fake Tweet?
                  </p>

                </div>

                {/* <div className="tweet-container">   

                    <div onClick={()=> this.counter(0)}>
                      <TweetBox text={this.state.allTweets[0]}/>
                    </div> 

                    <div onClick={()=> this.counter(1)}>
                      < TweetBox text={this.state.allTweets[1]}/>
                    </div>

                    <div onClick={()=> this.counter(2)}>
                      <TweetBox text={this.state.allTweets[2]}/>
                    </div> 
                </div>  */}

                {this.renderTweetBox()}

                {/* <div className="tweet-container">
                 <TweetBox text={this.state.fakeTweet}/>

                 {this.renderTweetBox()}



                </div> */}

                <div className="right-panel">
                    <p className="acc">
                      Accuracy: {this.state.accuracy}%
                    </p>
                    <p>
                      Correct: {this.state.correct}  
                      &nbsp;&nbsp;&nbsp;  
                      Total: {this.state.total} 
                    </p>

                    <br></br>
                    {/* <button className="reload-button" onClick={() => this.componentDidMount()}> Reload </button>       */}

                    <button className="reload-button" onClick={() => this.getTweets()}> Reload </button>      

                </div>

              </div>
          </div>
      );
    }
  } 


export default App;
