import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import bird from './component/trumpBird1.png';



function App() {
  const [currentTime, setCurrentTime] = useState(0);

  const [tweet, setTweet] = useState("");

  const [random, setRandom] = useState("");
  const [random2, setRandom2] = useState("");



  useEffect(() => {
    fetch('/time').then(res => res.json()).then(data => {
      setCurrentTime(data.time);
    });
  }, []);


  useEffect(() => {
    fetch('/tweet').then(res => res.json()).then(data => {
      setTweet(data.tweet);
    });
  }, []);

  useEffect(() => {
    fetch('/random').then(res => res.json()).then(data => {
      setRandom(data.random);
    });
  }, []);

  useEffect(() => {
    fetch('/random').then(res => res.json()).then(data => {
      setRandom2(data.random2);
    });
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={bird} className="trump-bird" alt="logo" />

        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        {/* <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a> */}

        {/* <p>The current time is {currentTime}.</p> */}
        <p>Tweet: {tweet}</p>
        <p>Tweet: {random}</p>
        <p>Tweet: {random2}</p>



      </header>
    </div>
  );
}

export default App;
