    });
  }, []);

  useEffect(() => {
    fetch('/random').then(res => res.json()).then(data => {
      setRandom(data.random);
    });
  }, []);

  useEffect(() => {
    fetch('/random').then(res => res.json()).then(data => {
      setRandom2(data.random);
    });
  }, []);


  function renderTweet(i){
    return <TweetBox text={i} />;
  }



  return (
    <div className="App">
      <header className="App-header">
        <img src={bird} className="trump-bird" alt="logo" />


        <div>
          {renderTweet(tweet)}
          {renderTweet(random)}
          {renderTweet(random2)}
        </div>



        {/* <p>Tweet: {tweet}</p>
