                  {this.returnTest()}
                  </p>


                  <p>Total: {this.state.total} </p>
                </div>


                <div className="tweet-container">
                  {this.renderTweetBox(this.state.fakeTweet)}
                  {/* {this.renderTweetBox(this.state.firstRandom)}
                  {this.renderTweetBox(this.state.secondRandom)} */}
                </div>

                <div className="right-panel">
